import { useEffect, useState, setState } from "react";
import WeatherDay from "./components/WeatherDay";

const App = () => {
  const LOCATION_KEY = "217943_PC";
  const API_KEY = "KmwaoZaAqRhBNTTE8UoFIo4EqSavfDGu ";

  const [fiveDays, setfiveDays] = useState();
  useEffect(() => {
    fetch(
      `http://dataservice.accuweather.com/forecasts/v1/daily/5day/locationKey=${LOCATION_KEY}?apikey=${API_KEY}`
    )
      .then((res) => res.json())
      .then((res) =>
        setfiveDays(
          res.DailyForecasts.map((fi) => {
            return {
              date: fi.Date,
              icon: fi.Day.Icon,
              status: fi.Day.IconPhrase,
              max: fi.Temperature.Maximum.Value,
              min: fi.Temperature.Minimum.Value,
            };
          })
        )
      );
  }, []);

  useEffect(() => {
    console.log(fiveDays);
  }, [fiveDays]);

  return (
    <>
      <h1 className="App">By the Name Of Allah</h1>
      {!!fiveDays &&
        fiveDays.map((f) => {
          <div key={f.Date}>
            <WeatherDay
              min={f.min}
              max={f.max}
              date={f.date}
              icon={f.icon}
              status={f.status}
            />
          </div>;
        })}
    </>
  );
};

export default App;
