    fetch(
      `http://dataservice.accuweather.com/forecasts/v1/daily/5day/locationKey=${LOCATION_KEY}?apikey=${API_KEY}`
    )
      .then((res) => res.json())
      .then((res) =>
        setState(
          res.map((r) => {
            r;
          })
        )
      );
  }, []);

  return (
    <>
      <h1 className="App">By the Name Of Allah</h1>
    </>
  );
};

export default App;
